/*
const int FREE = 0;
const int NEAR_DANGER = 1;
const int CALLED = 2;
const int FINISHED = 3;
*/
const int FINISHED = -1;
const int FREE = 8;
const int CALLED = 9;
const int NEAR_DANGER = 10;

clock x;
int id, k;                           // Id and trajectory index
pos_t pos;                           // Position in the map
pos_t trajectory[MAX_TRAJ];          // Trajectory
int traj_l;                          // Trajectory length
int status = FREE;                   // Status
int time_to_assist = -1;             // Time to rescue a civilian       
int civ_id;                          // Id of the civilian rescue


/*
 * Check if near a Civilian in danger
 */
bool nearCivilianInDanger() {
    int i, j;
    pos_t civ_pos;
    for (j=-1; j<=1; j++) {
        for (i=-1; i<=1; i++) {
            if (pos.r+i >= 0 && pos.c+j >= 0 && pos.r+i < GRID_HEIGHT && pos.c+j < GRID_LENGTH && cell_int(pos.r+i, pos.c+j) == NEED_ASSIST){
                civ_pos.r = pos.r+i;
                civ_pos.c = pos.c+j;
                civ_id = get_civ_idx(civ_pos);
                return true;
            }
        }
    }
    return false;
}

/*
 * Enter the map at the entry point.
 */
void enter() {
    id = p_id;
    pos = pos_FRs[id];
    if (nearCivilianInDanger()) {
        status = NEAR_DANGER;
    }
}

/*
 * Calculate total time to assist a civilian in danger
 */
void timeToAssist() {
    if (status == NEAR_DANGER) {
        time_to_assist = T_FR;
    } else {
        time_to_assist = T_FR + dist(pos, pos_civilians[civ_id]) + dist(pos_civilians[caller_id], pos);
    }
}


/*
 * Update global position Array
 */
void update_FR_pos() {
    pos_FRs[id] = pos;
}

/*
 * Get closer fire
 */
int getCloserFire(pos_t pos){
    int i, min = MAX_INT;
    int closer_fire = -1;
    for(i=0; i < N_EXITS; i++){
        if(dist(fires[i], pos) < min){
            min = dist(fires[i], pos);
            closer_fire = i;
        }
    }
    return closer_fire;
}

// TODO: da sistemare, aggiornare posizione se già vicino ad un fuoco
/*
 * Step in fire direction, greater probability to find a civilian to help
 */
void step_fire(){
    // Check if need to move
    if ((saved+casualties) == N_CIVILIANS) {
        status = FINISHED;
    } else {
        pos_t closest_fire = fires[getCloserFire(pos)], next = OUT_OF_MAP;
        if (pos != closest_fire) {
            getNextCellToGoTo(pos, closest_fire, next);
        } else {
            next = pos;
        }
        if(isCellFree(next)){
            grid[pos.r][pos.c] = NONE;
            pos = next;
            grid[pos.r][pos.c] = FIRST_R;
        }
        update_FR_pos();
    }
}


void movementPolicy(){
    int tries = 0;
    bool valid_step_found = false;

    int step_dir = 0;
    if(fr_movement_policy == RANDOM){

      while (valid_step_found == false && tries < 8) {
        if (step_dir == 0 && pos.r+1 < GRID_HEIGHT && cell_int(pos.r+1, pos.c) == NONE) { // Move down
            grid[pos.r][pos.c] = NONE;
            pos.r++;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        if (step_dir == 1 && pos.r-1 >= 0 && cell_int(pos.r-1, pos.c) == NONE) { // Move up
            grid[pos.r][pos.c] = NONE;
            pos.r--;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        if (step_dir == 2 && pos.c-1 >= 0 && cell_int(pos.r, pos.c-1) == NONE) { // Move left
            grid[pos.r][pos.c] = NONE;
            pos.c--;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        if (step_dir == 3 && pos.c+1 < GRID_LENGTH && cell_int(pos.r, pos.c+1) == NONE) { // Move right
            grid[pos.r][pos.c] = NONE;
            pos.c++;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        if (step_dir == 4 && pos.r+1 < GRID_HEIGHT && pos.c-1 >= 0 && cell_int(pos.r+1, pos.c-1) == NONE) { // Move down-left
            grid[pos.r][pos.c] = NONE;
            pos.r++;
            pos.c--;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        if (step_dir == 5 && pos.r+1 < GRID_HEIGHT && pos.c+1 < GRID_LENGTH && cell_int(pos.r+1, pos.c+1) == NONE) { // Move down-right
            grid[pos.r][pos.c] = NONE;
            pos.r++;
            pos.c++;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        if (step_dir == 6 && pos.r-1 >= 0 && pos.c-1 >= 0 && cell_int(pos.r-1, pos.c-1) == NONE) { // Move up-left
            grid[pos.r][pos.c] = NONE;
            pos.r--;
            pos.c--;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        if (step_dir == 7 && pos.r-1 >= 0 && pos.c+1 < GRID_LENGTH && cell_int(pos.r-1, pos.c+1) == NONE) { // Move up-right
            grid[pos.r][pos.c] = NONE;
            pos.r--;
            pos.c++;
            grid[pos.r][pos.c] = FIRST_R;
            valid_step_found = true;
        }
        step_dir = (step_dir+1) % 8;
        if (valid_step_found == false) {
            tries++;
        }
        }
    }

}

// TODO: change to put moveFr??
void step() {

    // Check if need to move
    if ((saved+casualties) == N_CIVILIANS) {
        status = FINISHED;
    } else {
        // Random policy
        movementPolicy();

        update_FR_pos();

        if (nearCivilianInDanger()) {
            status = NEAR_DANGER;
        }
    }
}


// TODO capire come gestire la precenza tra near danger e la ricezione della richiesta di aiuto: dovrebbe essere l'ordine di dichiarazione dei chan
/*
 * Update global grid by setting FR busy or not
 */
void updateGrid() {
    if (grid[pos.r][pos.c] == FIRST_R && status == NEAR_DANGER) {
        grid[pos.r][pos.c] = FIRST_R_BUSY_NEAR_DANGER;
    } else if (grid[pos.r][pos.c] == FIRST_R && status == CALLED) {
        grid[pos.r][pos.c] = FIRST_R_BUSY_CALLED;
    } else if (grid[pos.r][pos.c] == FIRST_R_BUSY_NEAR_DANGER || grid[pos.r][pos.c] == FIRST_R_BUSY_CALLED) {
        grid[pos.r][pos.c] = FIRST_R;
    }
}

