/*
 * Update when a help request is received
 */
void helpReqReceived() {
    status = CALLED;
    updateGrid();
    civ_id = get_civ_idx(pos_to_save);
    pos_to_save = OUT_OF_MAP;
}

/*
 * Update when starting to help someone near
 */
void nearDanger() {
    x = 0;
    updateGrid();
    timeToAssist();
    pos_to_save = OUT_OF_MAP;
}

/*
 * Update when returning to Moving state
 */
void retToMove() {
    status = FREE;
    x = 0;
    updateGrid();
    time_to_assist = -1;
}

