const int SAVED = -1;
const int DEAD = -1;
const int STEP = 3;
const int ZERO_RESP = 4;
const int DANGER = 5;
const int CIV_ASSISTED = 6;
const int BUSY = 7;

clock x;
int id;                    // Id
pos_t pos;                 // Position
int status = 0;            // Status
int timeToAssist = -1;     // Time to rescue another civilian
int civ_need_id;           // Id of the civilian to help (ZR case)
int fr_id;                 // Id of the FR to call
